mapper.select:
SELECT         

FROM project t
WHERE id = #{id}

select
t.operation_log
from task t

	t.project_id = #{projectId}

	  and t.create_date >= #{startDate}



		and t.create_date <= #{endDate}



order by t.create_date desc

select
(select count(ut.team_id) from user_team ut where ut.user_id = #{userId}) as "teamCnt",
(select count(pt.project_id) from project_team pt
    left join user_team sub_ut on sub_ut.team_id = pt.team_id where sub_ut.user_id = #{userId}
    ) as "projectCnt"

mapper.update:
UPDATE project
project_name = #{projectName}, project_remark = #{projectRemark}, current_version = #{currentVersion}, gitea_account = #{giteaAccount}, gitea_id = #{giteaId}, gitea_username = #{giteaUsername}, create_by = #{createBy}, create_date = #{createDate}, update_by = #{updateBy}, update_date = #{updateDate}, history_member = #{historyMember},
WHERE id = #{id}
