SELECT t.id , t.task_name , t.task_type , t.task_status , t.serial_number , t.current_operator , t.participant , t.correlation_ids , t.create_by , t.create_date , t.update_by , t.update_date , t.team_id , t.project_id , u.user_name as "currentOperatorName" , (SELECT sub_u.user_name FROM user sub_u where t.create_by = sub_u.id) as "createUserName" FROM task t left join user u on t.current_operator = u.id
t.create_by = #{params.createBy}
and t.create_date >= #{params.startDate}
and t.create_date <= #{params.endDate}
