SELECT t.id , t.task_name , t.task_type , t.task_status , t.serial_number , t.current_operator , t.participant , t.correlation_ids , t.create_by , t.create_date , t.update_by , t.update_date , t.team_id , t.project_id FROM task t where t.id IN
#{item}
