mapper.update:
UPDATE task
task_name = #{taskName}, task_type = #{taskType}, task_status = #{taskStatus}, remark = #{remark}, operation_log = #{operationLog}, serial_number = #{serialNumber}, current_operator = #{currentOperator}, participant = #{participant}, correlation_ids = #{correlationIds}, create_by = #{createBy}, create_date = #{createDate}, update_by = #{updateBy}, update_date = #{updateDate}, team_id = #{teamId}, project_id = #{projectId},
WHERE id = #{id}

UPDATE task t SET t.correlation_ids = CONCAT(IF(t.correlation_ids is null, '', CONCAT(t.correlation_ids, ',')),#{context})
WHERE t.id IN

	#{item}

UPDATE task t SET t.task_status = #{status}
WHERE t.id IN

	#{item}

UPDATE task t SET t.operation_log = CONCAT(IF(t.operation_log is null, '', t.operation_log),#{context}) WHERE t.id IN
#{item}
AND t.task_status != 'close'
