mapper.select:
SELECT         

FROM task t
WHERE id = #{id}

SELECT t.id , t.task_name , t.task_type , t.task_status , t.serial_number , t.current_operator , t.participant , t.correlation_ids , t.create_by , t.create_date , t.update_by , t.update_date , u.user_name as "currentOperatorName" , (SELECT sub.user_name FROM user sub where t.create_by = sub.id) as "createUserName" FROM task t left join user u on t.current_operator = u.id
t.create_by = #{params.createBy}
and t.create_date >= #{params.startDate}
and t.create_date <= #{params.endDate}
and t.task_status = #{params.taskStatus}
and t.task_type = #{params.taskType}
and t.task_name like CONCAT(CONCAT('%', #{params.taskName}), '%')
and t.serial_number like CONCAT(CONCAT('%', #{params.serialNumber}), '%')
ORDER BY t.create_date DESC

SELECT t.id , t.task_name , t.task_type , t.task_status , t.serial_number , t.current_operator , t.participant , t.correlation_ids , t.create_by , t.create_date , t.update_by , t.update_date FROM task t where t.id IN
#{item}
ORDER BY t.create_date DESC

mapper.update:
UPDATE task
task_name = #{taskName}, task_type = #{taskType}, task_status = #{taskStatus}, remark = #{remark}, operation_log = #{operationLog}, serial_number = #{serialNumber}, current_operator = #{currentOperator}, participant = #{participant}, correlation_ids = #{correlationIds}, create_by = #{createBy}, create_date = #{createDate}, update_by = #{updateBy}, update_date = #{updateDate},
WHERE id = #{id}

UPDATE task t SET t.correlation_ids = CONCAT(IF(t.correlation_ids is null, '', CONCAT(t.correlation_ids, ',')),#{context})
WHERE t.id IN

	#{item}

UPDATE task t SET t.task_status = #{status}
WHERE t.id IN

	#{item}

UPDATE task t SET t.operation_log = CONCAT(IF(t.operation_log is null, '', t.operation_log),#{context}) WHERE t.id IN
#{item}
AND t.task_status != 'close'
